node * list_cut_minmax(node * ls) {
/* We also need pointer to previous nodes to keep the list linked */
node *curr_max, *prev_max;
node *prev_min, *curr_min;
node *curr, *prev;		/* to browse the list */
if (ls == NULL)			/* empty list: nothing to do */
return NULL;
/* Initialization */
curr_max = curr_min = ls;
prev_max = prev_min = NULL;
/* Browse from ls->next as the head element is checked already */
for (prev=ls, curr=ls->next; curr!=NULL; prev=curr, curr=curr->next) {
if(curr->value > curr_max->value) {
curr_max = curr;
prev_max = prev;
}
if(curr->value < curr_min->value) {
curr_min = curr;
prev_min = prev;
}
}
/* If min==max, all nodes are the same, we just erase the head */
if (curr_min == curr_max) {
ls = curr_min->next;
free(curr_min);
return ls;
}
/* Erasing min node first (this is an arbitrary choice) */
if (prev_max == curr_min)	/* if min->max, we need to update prev_max */
prev_max = prev_min;
if (prev_min == NULL)		/* Erasing the head */
ls = curr_min->next;
else				/* Erasing non-head nodes */
prev_min->next = curr_min->next;
free(curr_min);
/* Now erasing max node */
if (prev_max == NULL)
ls = curr_max->next;
else
prev_max->next = curr_max->next;
free(curr_max);
return ls;
}

node * list_shift(node * ls, unsigned int n)
{
node * cur;
unsigned int len;
/* Easy exit conditions */
if (n == 0)
return ls;
if (ls == NULL || ls->next == NULL)
return ls;
/* Count the nodes in the list */
for (cur = ls, len = 1; cur->next != NULL; cur = cur->next, len++);
/* We can use the remainder of n modulo len */
n = n % len;
if (n == 0)
return ls;
/* Make the list circular */
cur->next = ls;
/* n becomes the number of iterations to find the new head from ls */
n = len-n-1;
for (cur = ls; n>0; cur = cur->next, n--);
/* Now cur points to the node preceding the new head: we need it to close the list */
ls = cur->next;		/* new head */
cur->next = NULL;	/* close the list */
return ls;
}

